import React, { useState } from "react";
import AppContext from "./AppContext";
const AppProvider = (props) => {
  const [burgerAction, setBurgerAction] = useState(false);
  return (
    <AppContext.Provider value={(setBurgerAction, burgerAction)}>
      {props.children}
    </AppContext.Provider>
  );
};

export default AppProvider;
