import styled from "styled-components";
const FormControl = styled.div`
  display: flex;
  flex-direction: column;
  padding: 10px;
  
  & h3 {
      color: ${props=>props.invalid ? 'red' : 'black'}
  }
  & input {
    height: 2.5rem;
    width: 100%;
    margin-bottom: 1rem;
    background: ${props=>props.invalid ? 'red' : 'white'}
  }
`;
export default FormControl;
