import "./NewGoal.css";
import { useState } from "react";
function NewGoal(props) {
    const [goalTitle, setGoalTitle] = useState('')
    const [isValid, setIsValid] = useState(true);
  const onSubmitHandler = (event) => {
    event.preventDefault();
    if(goalTitle.trim().length === 0){
      setIsValid(false);
        return;
    }
    props.onAddNewGoal(goalTitle);
    setGoalTitle('');
  };
  const onChangeHandler = (event) => {
    if(event.target.value.trim().length > 0){
      setIsValid(true);
    }
    setGoalTitle(event.target.value);
  };
  return (
    <form onSubmit={onSubmitHandler} className="goal-new">
      <h3 style={{color: isValid ? 'black': 'red'}}>Enter Goal</h3>
      <input type="text" onChange={onChangeHandler} value={goalTitle} maxLength="50"/>
      <button type="submit">Add Goal</button>
    </form>
  );
}
export default NewGoal;
