import React, { useContext } from "react";
import TypeWriterEffect from "react-typewriter-effect";
import AppContext from "../../../Context/AppContext";
const Info = () => {
  const { action } = useContext(AppContext);
  const writerStyle = {
    color: "white",
    fontSize: "1.1rem",
    margin: "0px 7px",
    fontWeight: "400"
  };
  return (
    <div>
      <h3>
        <TypeWriterEffect
          textStyle={
            action.darkMode ? writerStyle : { ...writerStyle, color: "black" }
          }
          startDelay={900}
          cursorColor="blue"
          text="Hi! My name is Suraj Poddar. I'm a Frontend Developer.
          I live in Prayagraj City, and I'm currently a second year student pursuing Bachelor's degree in Computer Science from Kalinga Institute of Industrial Technology.
          I love singing song and watching movies."
          nextTextDelay={1000}
          typeSpeed = {30}
        />
      </h3>
    </div>
  );
};

export default Info;
