import React, { useContext } from "react";
import TypeWriterEffect from "react-typewriter-effect";
import AppContext from "../../../Context/AppContext";
import Style from "./Info.module.css";
const Info = () => {
  const { action } = useContext(AppContext);
  const writerStyle = {
    color: "white",
    fontSize: "1.1rem",
    margin: "0px 7px",
    fontWeight: "400"
  };
  return (
    <div className={Style["info"]} >
        <TypeWriterEffect
          textStyle={
            action.darkMode ? writerStyle : { ...writerStyle, color: "black" }
          }
          startDelay={900}
          cursorColor="blue"
          text="Hi! \n My name is Suraj Poddar. I'm a Frontend Developer.\n
          I live in Prayagraj City, and I'm currently a second year student pursuing Bachelor's degree in Computer Science from Kalinga Institute of Industrial Technology.\n
          I love singing song and watching movies."
          nextTextDelay={1000}
          typeSpeed = {100}
        />
    </div>
  );
};

export default Info;
