import styled from "styled-components";
const FormControl = styled.div`
  display: flex;
  flex-direction: column;
  padding: 10px;

  & input {
    height: 2.5rem;
    width: 100%;
    margin-bottom: 1rem;
  }

  & button {
    width: 24%;
    height: 2rem;
    font-weight: bold;
    border-radius: 5px;
    background: #ff8787;
    cursor: pointer;
    margin: 0px 0px 10px 9px;
  }
  & button:hover,
  & button:active {
    background: #d36363;
  }
`;
