import React from "react";
import emailjs from "emailjs-com";
const SendMail = () => {
  function sendEmail(e) {
    e.preventDefault();

    emailjs
      .sendForm("gmail", "template_bpz9mfy", form.current, "-7UwAWTNGZaq7H5Kr")
      .then(
        (result) => {
          console.log(result.text);
        },
        (error) => {
          console.log(error.text);
        }
      );
    e.target.reset();
  }
  return (
    <div>
      <formm onSubmit={sendEmail}>
        <input type="text" />
      </formm>
    </div>
  );
};

export default SendMail;
