import GoalBox from "./Components/GoalBox";
import GoalItemList from "./Components/GoalItemList";
import {useState} from "react";
let initialGoal = [
  {id: 'g1', title:'Reading Book'},
  {id: 'g2', title:'Watching Movies'},
  {id: 'g3', title:'Riding Bike'},
]
function App() {
  const [goals, setGoals] = useState(initialGoal);
  const addNewGoalHandler = (enteredGoal)=>{
    setGoals((prevGoal)=>{
      const newGoal = {id: Math.random(), title: enteredGoal};
      return [newGoal, ...prevGoal];
    })
  }
  return (
    <div>
        <GoalBox addNewGoal={addNewGoalHandler}/>
        <GoalItemList items={goal}/>
    </div>
  );
}

export default App;
