import React, { useContext, useEffect, useState } from "react";
import HomeIcon from "./svg/HomeIcon";
import ContactIcon from "./svg/ContactIcon";
import PortfolioIcon from "./svg/PortfolioIcon";
import AboutIcon from "./svg/AboutIcon";
import Style from "./Navigation.module.css";
import AppContext from "../Context/AppContext";
import { useNavigate } from "react-router-dom";
const Navigation = () => {
  const { action } = useContext(AppContext);
  const navigate = useNavigate();
  const [activeBtn, setActiveBtn] = useState(1);
  const onClickHandler = (active, path) => {
    navigate(path);
    setActiveBtn(active);
  };
  return (
    <nav
      className={`${Style["navbar"]} ${
        Style[action.burger ? "navbar_active" : ""]
      }`}
    >
      <ul>
        <li
          onClick={() => onClickHandler(1, "/")}
          className={Style[active === 1 ? "active" : ""]}
        >
          <HomeIcon />
          <span>Home</span>
        </li>
        <li onClick={() => onClickHandler(2, "/about")}>
          <AboutIcon />
          <span>About</span>
        </li>
        <li>
          <PortfolioIcon />
          <span>Portfolio</span>
        </li>
        <li>
          <ContactIcon />
          <span>Contact</span>
        </li>
      </ul>
      <footer className={Style["footer"]}>
        <p>Made By Suraj Poddar ðŸ˜‰</p>
      </footer>
    </nav>
  );
};

export default Navigation;
