import styled from "styled-components";
const FormControl = styled.div`
  display: flex;
  flex-direction: column;
  padding: 10px;

  & input {
    height: 2.5rem;
    width: 100%;
    margin-bottom: 1rem;
  }
`;
export default FormControl;
