import React, { useContext } from "react";
import HomeIcon from "./svg/HomeIcon";
import ContactIcon from "./svg/ContactIcon";
import PortfolioIcon from "./svg/PortfolioIcon";
import AboutIcon from "./svg/AboutIcon";
import Style from "./Navigation.module.css";
import AppContext from "../Context/AppContext";
import { useNavigate } from "react-router-dom";
const Navigation = () => {
  const { action } = useContext(AppContext);
  const navigate = useNavigate();

  // const color = getComputedStyle(document.documentElement).getPropertyValue(
  //   "--background-light-color"
  // );
  const backgroundColor = getComputedStyle(document.documentElement);
  if(action.darkMode){
    document.documentElement.style.setProperty("--background-dark-light-color","lightgray")
    // document.documentElement.style.setProperty("--font-dark-light-color","rgb(37, 37, 37)")
  }
  else{
    document.documentElement.style.setProperty("--background-dark-light-color","rgb(37, 37, 37)")
    // document.documentElement.style.setProperty("--font-dark-light-color","white")
  }
  return (
    <nav
      className={`${Style["navbar"]} ${
        Style[action.burger ? "navbar_active" : ""]
      } ${Style[action.darkMode ? "navbar_dark" : ""]}`}
    >
      <ul>
        <li onClick={() => navigate("/")}>
          <HomeIcon />
          <span>Home</span>
        </li>
        <li onClick={() => navigate("/about")}>
          <AboutIcon />
          <span>About</span>
        </li>
        <li>
          <PortfolioIcon />
          <span>Portfolio</span>
        </li>
        <li>
          <ContactIcon />
          <span>Contact</span>
        </li>
      </ul>
      <footer className={Style["footer"]}>
        <p>@2021 My Portfolio Website</p>
      </footer>
    </nav>
  );
};

export default Navigation;
