import React from "react";
import emailjs from "emailjs-com";
import Style from "./SendMail.module.css";
const SendMail = () => {
  function sendEmail(e) {
    e.preventDefault();

    emailjs
      .sendForm("gmail", "template_bpz9mfy", e.target, "-7UwAWTNGZaq7H5Kr")
      .then(
        (result) => {
          console.log(result.text);
        },
        (error) => {
          console.log(error.text);
        }
      );
    e.target.reset();
  }
  return (
    <div className={Style["mail"]}>
      <form onSubmit={sendEmail}>
        <div className={Style["mail_content"]}>
          <div className={Style["mail_content__info"]}>
            <div>
              <input type="text" placeholder="Name" name="name" />
            </div>
            <div>
              <input type="email" placeholder="Email" name="email" />
            </div>
            <div>
              <input type="text" placeholder="Subject" name="subject" />
            </div>
          </div>
          <div>
            <div>
              <textarea rows="8" cols="30" placeholder="Your Message" height />
            </div>
            <div>
              <input type="submit" value="Send Message"></input>
            </div>
          </div>
        </div>
      </form>
    </div>
  );
};

export default SendMail;
